import turtle
import random

def round (t, a, r, g, b):
    """
    Draws a circle and fills in with any color.
    """
    t.penup()
    t.pendown()
    turtle.colormode(255)
    t.begin_fill()
    t.circle(radius = a)
    t.color(r, g, b)
    t.end_fill()
    t.penup()

    # ....


def quad (t, l, w, r, g, b):
    """
        Make a qualuilatural shape (rectangle or squares can be made.
    """
    t.penup()
    t.pendown()
    t.begin_fill()
    for q in range(2):
        t.forward(l)
        t.right(90)
        t.forward(w)
        t.right(90)
    t.color(r, g, b)
    t.end_fill()
    t.penup()

    # ...
def arc (t, a, c, s):
    """
        makes arcs/curves
        """
    t.penup()
    t.pendown()
    t.pensize(s)
    t.circle(radius = a, extent = c)
    t.penup()

