def main():
    """
    draw the froggy boyo and fly
    """
    wn = turtle.Screen()
    jimmy = turtle.Turtle()

    wn.bgcolor("cyan") #change color

    jimmy.speed(0)
    water(jimmy)

    #head
    round(jimmy,100, 100, 255, 0)
    #eyel
    jimmy.pencolor(0,0,0)
    jimmy.goto(-75 ,150)
    round(jimmy,35, 255,255,255)
    round(jimmy, 15,0,0,0)
    #eyer
    jimmy.goto(75, 150)
    round(jimmy, 35, 255, 255, 255)
    round(jimmy, 15, 0, 0, 0)
    #mouth
    jimmy.goto(-70,100)
    jimmy.right(90)
    arc(jimmy, 70,180,3)
    #tongue
    jimmy.pensize(1)
    jimmy.left(90)
    jimmy.goto(20,-120)
    quad(jimmy, 40,150,255,150,255)

    #fly
    jimmy.goto(0,-100)
    round(jimmy, 30,100,100,100)
    jimmy.goto(0,-120)
    for eyes in range(2):
        round(jimmy, 15, 255,0,0)
        jimmy.forward(-30)
    jimmy.goto(-35,-120)
    for wings in range(2):
        jimmy.pencolor("#FFFFFF")
        arc(jimmy,20,270,4)
        jimmy.forward(25)

    wn.exitonclick()



main()  # Starts the program!