import turtle

def drawroof():

    t = turtle.Turtle()
    t.hideturtle()
    t.speed(0)
    t.penup()
    t.goto(-200, 50)
    t.pendown()
    t.color('red')
    t.begin_fill()
    for i in range(1):
        t.forward(450)
        t.left(110)
        t.forward(199)
        t.left(70)
        t.forward(300)
        t.left(66)
        t.forward(205)
    t.end_fill()
def drawroofwindow():
    t = turtle.Turtle()
    t.hideturtle()
    t.speed(0)
    t.color("white")
    t.penup()
    t.goto(-60, 100)
    t.pendown()
    t.pensize(5)
    t.forward(130)
    for i in range(2):
        t.left(90)
        t.forward(80)
        t.left(90)
        t.forward(130)
    t.backward(65)
    t.left(90)
    t.forward(80)
    t.backward(40)
    t.right(90)
    t.forward(60)
    t.backward(120)

