def bodyofhouse():
    t = turtle.Turtle()
    t.speed(0)
    t.penup()
    t.goto(-190, 50)
    t.pendown()
    t.hideturtle()
    t.color("#d2b48c")
    t.begin_fill()
    for i in range (1):
        t.forward(426)
        t.right(90)
        t.forward(256)
        t.right(90)
        t.forward(426)
        t.right(90)
        t.forward(256)
        t.end_fill()
