import turtle


def moon_code():
    """
    this function draws a circle with the moon color
    """
    moon = turtle.Turtle()
    moon.hideturtle()
    moon.speed(0)

    moon.penup()
    moon.goto(0, 300)                       # going up to draw the moon
    moon.pendown()

    moon.color('#dcdcdc')                         # color of the moon
    moon.begin_fill()
    moon.circle(60)                               # circle of the moon
    moon.end_fill()


def house_code():
    """
        this function draws the body of the house and fill it with wall color
    """
    house = turtle.Turtle()
    house.hideturtle()
    house.speed(0)

    house.penup()
    house.goto(-400, -250)
    house.pendown()

    house.color('#c3c4c2')                        # wall color of body house
    house.begin_fill()
    house.forward(800)                            # body of house starts here
    house.left(90)
    house.forward(300)
    house.left(90)
    house.forward(325)
    house.right(60)
    house.forward(150)
    house.left(120)
    house.forward(150)
    house.right(60)
    house.forward(325)
    house.left(90)
    house.forward(300)                            # body of house ends here
    house.end_fill()


