    roof.forward(60)
    roof.right(45)
    roof.forward(45)
    roof.right(90)
    roof.forward(43)
    roof.right(90)
    roof.forward(87)
    roof.right(-45)
    roof.backward(60)
    roof.right(135)
    roof.forward(700)
    roof.right(70)
    roof.forward(80)
    roof.left(75)
    roof.forward(40)
    roof.left(90)
    roof.forward(80)
    roof.right(95)
